CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `requirements_get_all` AS select `r`.`requirement_id` AS `requirement_id`,`r`.`project_id` AS `project_id`,`r`.`title` AS `title`,`r`.`description` AS `description`,`r`.`type` AS `type`,`r`.`rat` AS `rat`,`r`.`translated` AS `translated`,(select count(1) from `tasks` `t` where (`t`.`requirement_id` = `r`.`requirement_id`)) AS `tasks_count`,cast(((select sum(`t`.`percentage_completed`) from `tasks` `t` where (`t`.`requirement_id` = `r`.`requirement_id`)) / (select count(1) from `tasks` `t` where (`t`.`requirement_id` = `r`.`requirement_id`))) as decimal(10,1)) AS `percentage_completed` from `requirements` `r`;
